def countLangs(x):
    try:
        return len(x.split(","))
    except:
        return 0


def value_to_float(x):
    if 'K' in x:
        if len(x) > 1:
            return float(x.replace('K', '')) * 1000
        return 1000.0
    if 'M' in x:
        if len(x) > 1:
            return float(x.replace('M', '')) * 1000000
        return 1000000.0
    if 'B' in x:
        return float(x.replace('B', '')) * 1000000000
    return x






def clean(in_df):
    df = in_df.copy(deep=True)

    df['num_languages'] = df['languages'].apply(countLangs) 
    df['boolean_rank'] = df['rank'].notna() 
    df['app_rating'] = df['app_rating'].replace(np.nan,0)
    df['size'] = df['size'].apply(lambda x: re.match("\d+(\.)?\d*",x)[0])
    df['size'].astype(float) 
    df.assign(free_boolean =df['price']=='Free',inplace=True)  
    df['rating_count']=df['rating_count'][:].str.split(' ',n=1,expand = True)
    df['rating_count'].fillna("0",inplace=True)
    df['rating_count'] = df['rating_count'].apply(value_to_float) 
    df['rating_count']=df['rating_count'].astype(float)
    df['free_or_not']=df['price']!='Free' 
    df['price']=df.price.astype(str) 
    df['price'].fillna(value=0, inplace=True)
    df['languages']=df['languages'].astype(str) 
    df['languages'].apply(lambda x:x.replace('nan', "English"))




    return df

