# by key
def sort_dict(d):
    return dict(OrderedDict(sorted(d.items(), key=lambda t: t[0])))
# by value
def sort_dict_by_val(d):
    return [(k, d[k]) for k in sorted(d, key=d.get, reverse=True)]

