def list_all_years(df_ministro):
    anos = tuple(range(2009, 2021))
    vals = [0] * len(anos)
    serie = pd.Series(vals, index=anos)
    for ano in anos:
        if ano in df_ministro.index:
            serie[ano] += df_ministro[ano]
    return serie

