def normalize(msg):

    doc = nlp(msg)
    res=[]

    for token in doc:
        if(token.is_stop or token.is_digit or token.is_punct or not(token.is_oov)):
            pass
        else:
            res.append(token.lemma_.lower()) #Lower case of the token lemma is added

    return " ".join(res)

