def autolabel(graph):
    for patch in graph.patches:
        height = patch.get_height()
        plt.annotate(height.astype('int64'), xy = (patch.get_x() + patch.get_width()/2, height), xytext = (0, 5),
                    textcoords = 'offset points', ha = 'center', fontsize = 13)
