import pycountry

def country_code_to_name(country_code):

    if len(country_code) == 2:
        country = pycountry.countries.get(alpha_2=country_code)
    else:
        country = pycountry.countries.get(alpha_3=country_code)

    if not country:
        return 'Not Found'
    else:
        return country.name

