def dictionary_gen(title, iden):
    title_range = len(title)
    iden_range = len(iden)

    category_dict = {}
    if title_range == iden_range:
        for i in range(title_range):
            category_dict[int(iden[i])] = title[i]
    else:
        return(0)

    return category_dict

