# for converting numbers in string form to proper machine understood numerals
def decoma(x):
    if type(x) == str:
        symbols = [',' ,'$' , '<' , ">" , '+']
        for sym  in symbols:
            x = x.replace(sym , '')
        if len(x) <= 3:
            x += '000'
    return x

