from sklearn.metrics import mean_absolute_error

# Function for comparing different models
def score_model(model, X_t=X_train, X_v=X_valid, y_t=y_train, y_v=y_valid):
    model.fit(X_t, y_t)
    preds = model.predict(X_v)
    return mean_absolute_error(y_v, preds)

mae_dict = {}
for i in range(0, len(models)):
    mae = score_model(models[i])
    mae_dict[i+1] = mae # collect all the maes
    print("Model %d MAE: %d" % (i+1, mae))

# find the model with the smallest mae
sorted_mae = {key: val for key, val in sorted(mae_dict.items(), key = lambda ele:ele[1])}
min_mae = dict(list(sorted_mae.items())[0:1])
key, value = list(min_mae.items())[0]
print("Model {} has the smallest MAE {}".format(key,value))
