class Mish(nn.Module):
    def __init__(self):
        super().__init__()

    def forward(self, x):
        #inlining this saves 1 second per epoch (V100 GPU) vs having a temp x and then returning x(!)
        return x *( torch.tanh(F.softplus(x)))

def to_Mish(layers):
    for i in range(len(layers)):
        layer = layers[i]
        if isinstance(layer, nn.ReLU):
            layers[i] = Mish()

