x,y = input("Enter the Petal length and Petal Width :  ").split()
x = float(x)
y = float(y)
if x<2 and y<1:
    print("The Flower is Iris-Setosa")
elif 0.9<y<1.6 and 2<x<6:
    print("The Flower is Iris-Versicolor")
elif 1.6<y<2.6 and 4<x<8:
    print("The Flower is Iris-Virginica")
else:
    print("Out of Bounds")
