#load the image list
train_images = {}
with open("train-images-boxable-with-rotation.csv", "r") as f:
    for l in f:
        r = l.split(",")
        if r[0] == 'ImageID':
            continue
        train_images[r[2]] = r[0]

print("loaded {} image ids".format(len(train_images)))
