headers = {"User-Agent": "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/70.0.3538.77 Safari/537.36"}
def show_unfollowed_followers(username):
    url = "https://www.kaggle.com/"+username
    r = requests.get(url, headers=headers)
    h = BeautifulSoup(r.content, "html.parser")
    s = re.findall('"followers":.+"list":\[\{.+\}\]',h.__str__())[0]
    lists = re.findall("\[\{.+\}\]", s)[0]
    followers_list, following_list = lists.split('"following"')[0],lists.split('"following"')[2]
    followers, following = re.findall('"/\w+"',followers_list), re.findall('"/\w+"',following_list)
    follower_imgurls = re.findall(r'https:\/\/.*?kaggle-avatars.*?\w?\.\w{3}', followers_list)
    data = {}
    for username, imgurl in zip(followers, follower_imgurls):
        data[username]=imgurl
    not_followed_yet = set(followers).difference(set(following))

    for username in not_followed_yet:
        display(HTML(f"<a href='{'https://www.kaggle.com'+username[1:-1]}' target='blank_'><h4>@{username[2:-1]}</h4><img src=\'{data[username]}\' onmouseover=\'this.style.opacity=\"0.8\";\' onmouseout=\'this.style.opacity=\"1\";\'></a><hr>"))
