        b=str(netflix["listed_in"][i]).split(",")
        for j in range(len(b)):
            b[j]=b[j].lstrip()
        movie_genre+=b
top_movie_genre=pd.Series(movie_genre)
top_movie_genre.value_counts().head(5)

