def most_frequent(items):
    dict = {}
    count, result = 0, ''
    for item in reversed(items):
        dict[item] = dict.get(item, 0) + 1
        if dict[item] >= count :
            count, result = dict[item], item
    return(result)

items = ['Dog','Cat','Wolf','Dog']
print(most_frequent(items))
