# taking care of missing values
def missing(data):
    d = data.copy(deep = True)
    for c in data:
        if (data[c].dtype =='int64') or (data[c].dtype =='float64') : 
            if data[c].isnull().values.any():
                m = data[c].dropna().mean()
                d[c].fillna(m, inplace=True)
        else:          
            if data[c].isnull().values.any():
                m = data[c].dropna().mode()[0]
                d[c].fillna(m, inplace=True)
    return d

