def bs_replicate(data, func):
    samples_bs = np.random.choice(data, size=len(data))
    return func(samples_bs)
def draw_bs_replicate(data, func, size):
    replicates = np.empty(size)

    for i in range(size):
        replicates[i] = bs_replicate(data, func)
    return replicates
