in_bubble = in_bubble_regular.copy()
for player in in_bubble_playoff.PLAYER.tolist():#iterate through playoff data since there are less players
    if player not in in_bubble_regular.PLAYER.tolist():
        continue
    else:
        games_regular = float(in_bubble_regular.loc[(in_bubble_regular.PLAYER == player), 'GP'])
        games_playoff = float(in_bubble_playoff.loc[(in_bubble_playoff.PLAYER == player), 'GP'])
        #iterate through each columns except the player names
        for column in in_bubble.columns[1:]:
            if column == 'GP':
                in_bubble.loc[(in_bubble.PLAYER == player), 'GP'] = games_regular + games_playoff
            else:
                value_regular = float(in_bubble_regular.loc[(in_bubble_regular.PLAYER == player), column])
                value_playoff = float(in_bubble_playoff.loc[(in_bubble_playoff.PLAYER == player), column])
                value = round((value_regular * games_regular + value_playoff * games_playoff)/(games_playoff + games_regular),1)
                in_bubble.loc[(in_bubble.PLAYER == player), column] = value
