# Now! Let's run the game!!!!

board = ['#',' ',' ',' ',' ',' ',' ',' ',' ',' ']
while True:
    global board
    if not player_input():
        break

    while full_board_check(board) == False:
        player_choice(board)
        if win_check(board, marker) == True:
            print('Congratulation! You have won the game!')
            board = ['#',' ',' ',' ',' ',' ',' ',' ',' ',' ']
            break
        else:
            if marker == 'X':
                marker = 'O'
            else:
                marker = 'X'
    else:
        print("This game hadn't a winner")
    if not replay():
        break
