# Thanks to https://www.kaggle.com/rftexas/text-only-kfold-bert
def remove_punctuations(text):
    punctuations = '@#!?+&*[]-%.:/();$=><|{}^' + "'`"

    for p in punctuations:
        text = text.replace(p, f' {p} ')

    text = text.replace('...', ' ... ')

    if '...' not in text:
        text = text.replace('..', ' ... ')

    return text

