def month(x):
    if (x==1):
        return 'Jan'
    elif (x==2):
        return 'Feb'
    elif (x==3):
        return 'Mar'
    elif (x==4):
        return 'Apr'
    elif (x==5):
        return 'May'
    elif (x==6):
        return 'Jun'
    elif (x==7):
        return 'Jul'
    elif (x==8):
        return 'Aug'
    elif (x==9):
        return 'Sept'
    elif (x==10):
        return 'Oct'
    elif (x==11):
        return 'Nov'
    else:
        return 'Dec'

