def season(x):
    if(x=='Mar' or x=='Apr' or x=='May'):
        return 'Spring'
    elif(x=='Jun' or x=='Jul' or x=='Aug'):
        return 'Summer'
    elif(x=='Sept' or x=='Oct' or x=='Nov'):
        return 'Autumn'
    else:
        return 'Winter'
