def tfidf_preprocess(df: pd.DataFrame, column: str, embedding_dim: int=20)-> pd.DataFrame:
    # tfidfで変換した後に、SVDで時限削減する。
    tfidf_vec = TfidfVectorizer(ngram_range=(1,2))
    text_tfidf = tfidf_vec.fit_transform(df[column].fillna('').values.tolist())
    svd = TruncatedSVD(n_components=embedding_dim, algorithm='arpack',random_state=9999)
    return svd.fit_transform(text_tfidf)

class W2VSWEM(object):
    def __init__(self, word_list: List[str], embedding_dim:int=20):
        # word2vecで特徴量を作って、wrod2vecの平均値を計算する。
        self.word_list = word_list
        self.embedding_dim = embedding_dim
        self.__validation_type()
        self.__train_w2v()

    def __validation_type(self):
        if isinstance(self.word_list, pd.Series):
            self.word_list = self.word_list.tolist()
        if not isinstance(self.word_list, list):
            raise TypeError(f'you should use list object, however you are using {type(word_list)}.')

    def __train_w2v(self):
        self.w2v_model = Word2Vec(self.word_list, vector_size=self.embedding_dim)
        self.vocab = self.w2v_model.wv.key_to_index

    def _get_single_column_vec(self, words) -> np.array:
        result = []

        for word in words:
            if word in self.vocab:

                vector = self.w2v_model.wv[word]
                result.append(vector)
        result = np.array(result)
        return result

    def get_result(self):
        self.swem_result = np.zeros([len(self.word_list), self.embedding_dim])
        for num, i in enumerate(self.word_list):
            swem = self._get_single_column_vec(i)
            if len(swem):
                swem = np.mean(swem, axis=0)
            else:
                swem = np.zeros(self.embedding_dim)
            self.swem_result[num, :] = swem

