# Encoding sighting_time 
def sighting(val):
    if val == 'morning'+'\n'+'(6am-12pm)':
        return 1
    elif val == 'afternoon'+'\n'+'(12pm-4pm)':
        return 2
    elif val =='evening'+'\n'+'(4pm-9pm)':
        return 3
    elif val== 'nighttime'+'\n'+'(9pm-12am)':
        return 4
    else:
        return 5

df['sighting_time_enc'] = df['sighting_time'].apply(sighting)
