keyword_dict = {}
for i in tqdm_notebook(users[users['keyword_list'].apply(lambda x: len(x)) != 0]['keyword_list'].values):
    for j in range(0, len(i)):
        word = i[j]['keyword']
        cnt = i[j]['cnt']
        try:
            keyword_dict[word] += cnt
        except:
            keyword_dict[word] = cnt
