# Get Age type (https://www.statcan.gc.ca/eng/concepts/definitions/age2)

def get_age_type(x):
#     print(x)

    if(not x):
        return 'NA'

    if(x > 64):
        return 'Senior'

    if(x > 24):
        return 'Adult'

    if(x > 14):
        return 'Youth'

    return 'Child'
