def load_png_line(path):
    t_paths = sorted(
        glob.glob(os.path.join(path,"*")), 
        key=lambda x: x,
    )
    images = []
    for i,filename in enumerate(np.array(t_paths)):
        if i%2==0:
            data = cv2.imread(filename)
            if data.max() == 0:
                continue
            images.append(data)

    return images

