def get_reviews(reviews):
    if reviews.endswith('.0M'):
        reviews = reviews[:-3] + '000000'
        return int(reviews)
    else:
        return int(reviews)
playstore['Reviews'] = playstore['Reviews'].apply(get_reviews)

