def fixLocation(loc):
    if('aristarchus' in loc.lower()):
        return 'Aristarchus'
    elif('gassendi' in loc.lower()):
        return 'Gassendi'
    elif(loc.lower() == 'dark side'):
        return 'Dark Side'
    elif('plato' in loc.lower()):
        return 'Plato'
    elif(loc == 'nan'):
        return 'Unknown'
    elif('alphonsus' in loc.lower()):
        return 'Alphonsus'
    else:
        return loc

df['Location'] = df['Location'].apply(lambda x:fixLocation(str(x)))
