def f(row):
    if row['HDI for year'] < 0.55:
        text = "low"
    elif row['HDI for year'] < 0.7:
        text = "medium"
    elif row['HDI for year'] < 0.8:
        text = "high"
    else:
        text = "very high"
    return text

suicide["HDI level"] = suicide.apply(f,axis = 1)
