class Model(object):

    def evaluate(self, X, y):
        preds = self.prediction(X)
        ind = np.where((y!=0)|(preds!=0))
        return 100*np.sum(np.abs(preds[ind] - y[ind])/((np.abs(preds[ind]) + np.abs(y[ind]))/2))/len(y)

