def hello(name, loud=False):
    if loud:
        print('HELLO, %s!' % name.upper())
    else:
        print('Hello, %s' % name)

hello('Bob') # พิมพ์ "Hello, Bob"
hello('Fred', loud=True)  # พิมพ์ "HELLO, FRED!"

