def build_neighborhood(graph, start_qid, k_max):
    subnodes = set([start_qid])
    for k in range(k_max):
        nodes_to_add = set()
        for qid in subnodes:
            nodes_to_add.update(graph.neighbors(qid))
        subnodes.update(nodes_to_add)
    return graph.subgraph(subnodes)

