def add_attributes_to_graph(graph, item_df):
    for node in graph:
        graph.nodes[node]['qid'] = node
        graph.nodes[node]['label'] = item_df.loc[node, 'en_label']
        graph.nodes[node]['description'] = item_df.loc[node, 'en_description']
    return graph
