# function to create a time slot for various time period of day
def time_period(x):
    'divide the time of the day into four categories'
    if 2<= x < 5:
        return "Pre_Morning"
    elif 5 <= x < 10:
        return "Morning_Rush"
    elif 10 <= x < 17:
        return "Day_Time"
    elif 17 <= x < 22:
        return "Evening_Rush"
    else:
        return "Late_Night"

df['time_slot'] = df.pick_hour.apply(lambda x: time_period(x))

df.time_slot.value_counts()
