def should_hit(player_total, dealer_total, player_aces):
    """Return True if the player should hit (request another card) given the current game
    state, or False if the player should stay. player_aces is the number of aces the player has.
    """
    if player_total > 17 and player_aces > 0:
        hit = False
    elif dealer_total == 10 and player_total > 19:
        hit = False
    elif dealer_total == 9 and player_total > 18:
        hit = False
    elif dealer_total == 8 and player_total > 17:
        hit = False
    elif player_total > 14:
        hit = False
    else:
        hit = True

    return hit

