class SimplePricePolicy(BasePricePolicy):
    def __init__(self):
        super(SimplePricePolicy, self).__init__()

    def __call__(self, days_left, tickets_left, demand_level):
        if days_left > 1:
            qty = 10
        else:
            qty = min(tickets_left, demand_level / 2)

        price = demand_level - qty
        return price

