# gather train data into a dataframe
filenames = os.listdir(TRAIN_DIR )
categories = []
for filename in filenames:
    category = filename.split('.')[0]
    if category == 'dog':
        categories.append('dog')
    else:
        categories.append('cat')

all_df = pd.DataFrame({
    'filename': filenames,
    'category': categories
})

# gather test data into a dataframe
