import subprocess
def move_img(x,y,kind):
    for id_code ,diagnosis in tqdm(zip(x,y)):
        if diagnosis == 0:
            subprocess.call(['cp','../input/aptos2019-blindness-detection/{}_images/{}.png'.format(kind,id_code),'../data/{}/0/{}.png'.format(kind,id_code)])
