def values(v, props=''):
    return props if v < 10 else None


def highlight_max(s, props=''):
    return np.where(s == np.nanmax(s.values), props, '')

style_table = table.style.applymap(values, props='color:red;')\
              .applymap(lambda v: 'opacity: 80%;' if (v < 10) and (v > -3) else None)

style_table.apply(highlight_max, props='color:white;background-color:green', axis=0)
