import datetime

x = datetime.datetime.now()

def validate(date_text):
    try:
        datetime.datetime.strptime(date_text, '%Y-%m-%d')
    except ValueError:
        raise ValueError("Incorrect data format, should be YYYY-MM-DD")


def checkSpeed(speed, birthdayDate):
    sp1 = 60
    sp2 = 80
    validate(birthdayDate)
    if birthdayDate == x.strftime("%Y-%m-%d"):
        sp1 += 5
        sp2 += 5
    if speed <= sp1 :
        print("No Ticket")
    elif sp1 < speed <= sp2 :
        print("Small Ticket")
    else :
        print("Big Ticket")

checkSpeed(86, "2021-07-02")
